<?phpclass inicioController {    private $m;    private $post;    private $files;    private $response;    private $validateMessage;    function __construct($active = FALSE, $accion) {        $active ? true : die('ACCESS DENIED');        $className = (string) __CLASS__;        $className = str_replace("Controller", "Model", $className);        $this->m = new $className(true);        $this->post = $_POST;        $this->files = $_FILES;        $this->$accion();    }    public function consultar() {        $this->m->consultar($this->post);                return $this->Response();    }    public function guardar() {        if (!$this->validar()) {            $this->response = $this->validateMessage;            return $this->Response();        }        $this->m->guardar($this->post);        return $this->Response();    }    public function editar() {        if (!$this->validar()) {            $this->response = $this->validateMessage;            return $this->Response();        }        $res = $this->m->editar($this->post);        return $this->Response();    }    public function borrar() {        $res = $this->m->borrar($this->post);        return $this->Response();    }    private function validar() {        $data = $this->post;        if ($data['nombre'] === "") {            $this->validateMessage = "Debe escribir un nombre";            return false;        }                return true;    }        public function ejecutar(){        switch ($this->post['evento']){            case "doLogin":                $this->m->iniciarSesion($this->post);                                break;        }        return $this->Response();    }    private function Response($type = null) {        if (!$type){            $type = $this->m->getResult()['type'];        }        if (!$this->response){            $this->response = $this->m->getResult()['output'];        }        $response = [            "returned" => $ty