<?phprequire "app/controller/Controller.php";class index {    private $controlador;    function __construct() {        $this->controlador = new Controller();        $this->execute();    }    private function execute() {                $method = $_SERVER['REQUEST_METHOD'];        switch ($method) {            case "GET":                $this->cargarPagina();                break;            case "POST":                $this->startProcess();                break;        }    }    private function cargarPagina() {        $this->controlador->cargarPagina();    }    private function startProcess() {                $this->controlador->startProcess();    }        }new index();